data = [];
//init AI scan for 8 channels, 10 kHz sampling, 1 sec 
mdaq_ai_scan_init([1:8], 1, %F, 10000, 1)
for i=1:10
	[newdata res] = mdaq_ai_scan(1000, %T);
	data = [data; newdata];
	mprintf('read %d samples...\n', res);
end
mdaq_ai_scan_stop();

//show data
plot(data);

data = [];
res = 0;

//init AI scan for 8 channels, 10 kHz sampling, 1 sec 
mdaq_ai_scan_init([1:8], 1, %F, 10000, 1)

while res > -1 then  
	[newdata res] = mdaq_ai_scan(1000, %F); 
	if( res > 0 ) then 
		mprintf('read %d samples...\n', res)
		data = [data; newdata]; 
	end
end

mdaq_ai_scan_stop();

//show data
plot(data);
