refsynopsisdiv.synopsis:
data = mdaq_ai_read();
data = mdaq_ai_read(channels, ai_range, bipolar, differential);
data = mdaq_ai_read(link_id, channels, ai_range, bipolar, differential);

refsection.programlisting:
// mdaq_ai_read usage example
data = mdaq_ai_read([1 2 3 4 5 6 7 8], 10, %F);
mprintf("ADC data: %f %f %f %f %f %f %f %f \n", data)

// mdaq_ai_read usage example with link_id parameter
con = mdaq_open();
for i=1:1000
    data = mdaq_ai_read(con, [1 2 3 4 5 6 7 8], 10, %F);
    mprintf("ADC data: %f %f %f %f %f %f %f %f \n", data)
end
mdaq_close(con)
