refsection.programlisting:
aiData = [];
dataCount = 0;
mdaq_ai_scan_init(1:8, 1, %F, 10000, 1)
for i=1:10
	[data result] = mdaq_ai_scan(1000, %T);
	aiData = [aiData; data];
	dataCount = dataCount + result;
	mprintf('Acquired %d scans (total: %d scans)\n', result, dataCount);
end
mdaq_ai_scan_stop();
plot(aiData);
clear aiData;

aiData = [];
dataCount = 0;
mdaq_ai_scan_init(1:8, 1, %F, 10000, 1)
while(dataCount < 10000)
	[data result] = mdaq_ai_scan(1000, %F);
	aiData = [aiData; data];
	dataCount = dataCount + result;
	if result > 0 then
		mprintf('Acquired %d scans (total: %d scans)\n', result, dataCount);
	end
end
mdaq_ai_scan_stop();
plot(aiData);
clear aiData;
