refsynopsisdiv.synopsis:
mdaq_dio_write();
mdaq_dio_write(dio, state);
mdaq_dio_write(link_id, dio, state);

refsection.programlisting:
// mdaq_dio_write usage example
// mdaq_dio_set_func usage example
link_id = mdaq_open(); 
// set DIO1 to HIGH
mdaq_dio_write(link_id, 1, 1);
// set DIO1 to LOW
mdaq_dio_write(link_id, 1, 0);
mdaq_close(link_id);
